import React, { useRef, useState } from 'react';
import emailjs from '@emailjs/browser';
import './Contact.css';

const Contact = () => {
  const form = useRef();
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_kon5lig', 'template_l54puhr', form.current, '2dBRYMzyXb91ElJvO')
      .then(() => {
        setMessage('Message sent successfully!');
        setMessageType('success');
        setTimeout(() => setMessage(''), 5000);
      })
      .catch(() => {
        setMessage('Failed to send message.');
        setMessageType('error');
        setTimeout(() => setMessage(''), 5000);
      });

    e.target.reset();
  };

  return (
    <section id="contact">
      <h2>Contact Me</h2>
      <form ref={form} onSubmit={sendEmail}>
        <input type="text" name="user_name" placeholder="Your Name" required />
        <input type="email" name="user_email" placeholder="Your Email" required />
        <textarea name="message" placeholder="Your Message" required></textarea>
        <button type="submit">Send</button>
      </form>
      
      {message && (
        <div className={`message ${messageType}`}>
          {message}
        </div>
      )}
      
      {/* Optional: Add contact info section */}
      <div className="contact-info">
        <a href="mailto:your.email@example.com" className="contact-item">
          <span className="icon">üìß</span>
          <span>Email</span>
        </a>
        <a href="https://linkedin.com/in/yourprofile" className="contact-item">
          <span className="icon">üíº</span>
          <span>LinkedIn</span>
        </a>
        <a href="https://github.com/" className="contact-item">
          <span className="icon">üêô</span>
          <span>GitHub</span>
        </a>
      </div>
    </section>
  );
};

export default Contact;
